import { Directive, computed, signal } from '@angular/core';

@Directive({
  selector: '[adk-pagination]',
  exportAs: 'adkPagination',
  standalone: true,
})
export class AdkPagination {
  #page = signal(1);
  readonly page = computed(() => this.#page());
  readonly limit = signal(10);
  readonly total = signal(0);

  /**
   * Check if the current page is the first
   */
  first = computed(() => this.#page() === 1);

  /**
   * Check if the current page is the last
   */
  last = computed(() => this.#page() * this.limit() >= this.total());

  /**
   * Go to the next page
   */
  next(): void {
    if (this.last()) {
      throw new Error('You are already on the last page');
    }
    this.#page.update((page) => page + 1);
  }

  /**
   * Go to the previous page
   */
  previous(): void {
    if (this.first()) {
      throw new Error('You are already on the first page');
    }
    this.#page.update((page) => page - 1);
  }
}

import { Directive, computed, signal } from '@angular/core';

import { ID } from '../tw-form-ui/models';

@Directive({
    selector: '[adk-selection]',
    exportAs: 'adkSelection',
    standalone: true,
})
export class AdkSelection {
    #items = signal<Record<ID, boolean>>({});
    count = computed(() => Object.values(this.#items()).filter(Boolean).length);

    items = computed(() => Object.keys(this.#items()).filter((id) => this.#items()[id]));
    /**
     * Select multiple items
     * @param ids
     */
    select(...ids: ID[]): void {
        this.#items.update((items) => ids.reduce((accumulator, id) => ({ ...accumulator, [id]: true }), items));
    }

    /**
     * Deselect an item
     * @param id
     */
    deselect(id: ID): void {
        this.#items.update((items) => {
            const { [id]: _, ...rest } = items;
            return rest;
        });
    }

    findObj(val: string): any | undefined {
        let foundObj;
        this.#items.update((items) => {
            foundObj = items[val];
            return items;
        });
        return foundObj;
    }

    /**
     * Clear all selected items
     */
    clear(): void {
        this.#items.set({});
    }

    /**
     * Check if an item is selected
     * @param id
     */
    selected(id: ID): boolean {
        return this.#items()[id] ?? false;
    }

    /**
     * Check if object is selected
     */
    selectedObj(obj: any): boolean {
        const objStr = JSON.stringify(obj);
        return this.#items()[objStr] ?? false;
    }
}

import { Directive, computed, signal } from '@angular/core';

export interface SortableItem {
    id: string;
    [key: string]: any;
}

@Directive({
    selector: '[adk-sorting]',
    exportAs: 'adkSorting',
    standalone: true,
})
export class AdkSorting<T extends SortableItem> {
    #items = signal<T[]>([]);
    #sortKey = signal<string | null>(null);
    #sortDirection = signal<'asc' | 'desc'>('asc');

    sortedItems = computed(() => {
        const items = [...this.#items()];
        const key = this.#sortKey();
        const direction = this.#sortDirection();

        if (!key) return items;

        return items.sort((a, b) => {
            const aValue = a[key];
            const bValue = b[key];

            if (aValue < bValue) {
                return direction === 'asc' ? -1 : 1;
            } else if (aValue > bValue) {
                return direction === 'asc' ? 1 : -1;
            } else {
                return 0;
            }
        });
    });

    /**
     * Add items to the list
     * @param newItems
     */
    add(...newItems: T[]): void {
        this.#items.update((items) => [...items, ...newItems]);
    }

    /**
     * Set the sorting key and direction
     * @param key
     * @param direction
     */
    sort(key: string, direction: 'asc' | 'desc'): void {
        this.#sortKey.set(key);
        this.#sortDirection.set(direction);
    }

    /**
     * Clear the sorting
     */
    clearSort(): void {
        this.#sortKey.set(null);
        this.#sortDirection.set('asc');
    }

    /**
     * Get the current sorting key
     */
    getSortKey(): string | null {
        return this.#sortKey();
    }

    /**
     * Get the current sorting direction
     */
    getSortDirection(): 'asc' | 'desc' {
        return this.#sortDirection();
    }
}

