                    <div
                        role="button"
                        class="flex items-center w-full p-3 leading-tight transition-all rounded-lg outline-none text-start hover:bg-blue-gray-50 hover:bg-opacity-80 hover:text-blue-gray-900 focus:bg-blue-gray-50 focus:bg-opacity-80 focus:text-blue-gray-900 active:bg-blue-gray-50 active:bg-opacity-80 active:text-blue-gray-900"
                        [routerLink]="[nav.link]"
                        [routerLinkActive]="'active'"
                    >
                        <div class="grid mr-4 place-items-center">
                            <span class="material-symbols-outlined"> {{ nav.icon }} </span>
                        </div>
                    </div>
                    }
                </nav>
            </div>
            }
            <div class="relative h-full w-full">
                <ng-content [select]="'.sidebar-content'"></ng-content>
            </div>
        </div>
    `,
})
export class SidebarComponent implements OnChanges {
    @Input() title = 'Sidebar';
    @Input() navigation: { link: string; icon: string; text: string }[] = [];

    isExpanded = true;
    toggleSidebar = () => {
        ('SidebarComponent: toggleSidebar');
        this.isExpanded = !this.isExpanded;
    };

    ngOnChanges({ title, navigation }: SimpleChanges) {
        if (title) this.title = title.currentValue;
        if (navigation) this.navigation = navigation.currentValue;
    }
}

import { Component } from '@angular/core';

@Component({
    selector: 'tw-sortable-icon',
    standalone: true,
    template: `
        <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            stroke-width="2"
            stroke="currentColor"
            aria-hidden="true"
            class="w-4 h-4"
        >
            <path stroke-linecap="round" stroke-linejoin="round" d="M8.25 15L12 18.75 15.75 15m-7.5-6L12 5.25 15.75 9"></path>
        </svg>
    `,
    styles: [
        `
            :host {
                display: inline-block;
            }
        `,
    ],
})
export class SortableIconComponent {}

import type { Meta, StoryObj } from '@storybook/angular';

import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';

interface Item {
    name: string;
    age: number;
}

@Component({
    selector: 'app-sortable-table-example',
    template: `
        <table>
            <thead>
                <tr>
                    <th (click)="sort('name')">Name</th>
                    <th (click)="sort('age')">Age</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let item of sortedItems">
                    <td>{{ item.name }}</td>
                    <td>{{ item.age }}</td>
                </tr>
            </tbody>
        </table>
    `,
    standalone: true,
    imports: [CommonModule],
})
export class SortableTableExampleComponent {
    items: Item[] = [
        { name: 'John Doe', age: 28 },
        { name: 'Jane Smith', age: 34 },
        { name: 'Sam Green', age: 22 },
        { name: 'Emily Brown', age: 42 },
    ];

    sortedItems: Item[] = [...this.items];
    currentSortKey: keyof Item = 'name';
    currentSortDirection: 'asc' | 'desc' = 'asc';

    sort(key: keyof Item): void {
        if (this.currentSortKey === key) {
            this.currentSortDirection = this.currentSortDirection === 'asc' ? 'desc' : 'asc';
        } else {
            this.currentSortDirection = 'asc';
        }
        this.currentSortKey = key;
        this.sortedItems.sort((a, b) => {
            if (a[key] < b[key]) return this.currentSortDirection === 'asc' ? -1 : 1;
            if (a[key] > b[key]) return this.currentSortDirection === 'asc' ? 1 : -1;
            return 0;
        });
    }
}

const meta: Meta<SortableTableExampleComponent> = {
    component: SortableTableExampleComponent,
    title: '(TW) Angular UI / Directives / Sortable Table',
};
export default meta;
type Story = StoryObj<SortableTableExampleComponent>;

export const Primary: Story = {
    args: {},
};

