})
export class TwTypographyComponent implements OnChanges {
    @Input() variant: Variant = 'paragraph';
    @Input() color: Color | string = 'blue-gray-900';
    @Input() classStyle = '';

    get variantClass(): string {
        switch (this.variant) {
            case 'h1':
                return 'text-4xl';
            case 'h2':
                return 'text-3xl';
            case 'h3':
                return 'text-2xl';
            case 'h4':
                return 'text-xl';
            case 'h5':
                return 'text-lg';
            case 'h6':
                return 'text-base';
            case 'lead':
                return 'text-lg leading-relaxed';
            case 'paragraph':
                return 'text-base';
            case 'small':
                return 'text-sm';
            default:
                return 'text-base';
        }
    }

    get colorClass(): string {
        return `text-${this.color}`;
    }

    ngOnChanges(changes: SimpleChanges): void {
        if (changes['variant']) this.variant = changes['variant'].currentValue;
    }
}

