import type { Meta, StoryObj } from '@storybook/angular';

import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';

interface Item {
    name: string;
    age: number;
}

@Component({
    selector: 'app-sortable-table-example',
    template: `
        <table>
            <thead>
                <tr>
                    <th (click)="sort('name')">Name</th>
                    <th (click)="sort('age')">Age</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let item of sortedItems">
                    <td>{{ item.name }}</td>
                    <td>{{ item.age }}</td>
                </tr>
            </tbody>
        </table>
    `,
    standalone: true,
    imports: [CommonModule],
})
export class SortableTableExampleComponent {
    items: Item[] = [
        { name: 'John Doe', age: 28 },
        { name: 'Jane Smith', age: 34 },
        { name: 'Sam Green', age: 22 },
        { name: 'Emily Brown', age: 42 },
    ];

    sortedItems: Item[] = [...this.items];
    currentSortKey: keyof Item = 'name';
    currentSortDirection: 'asc' | 'desc' = 'asc';

    sort(key: keyof Item): void {
        if (this.currentSortKey === key) {
            this.currentSortDirection = this.currentSortDirection === 'asc' ? 'desc' : 'asc';
        } else {
            this.currentSortDirection = 'asc';
        }
        this.currentSortKey = key;
        this.sortedItems.sort((a, b) => {
            if (a[key] < b[key]) return this.currentSortDirection === 'asc' ? -1 : 1;
            if (a[key] > b[key]) return this.currentSortDirection === 'asc' ? 1 : -1;
            return 0;
        });
    }
}

const meta: Meta<SortableTableExampleComponent> = {
    component: SortableTableExampleComponent,
    title: '(TW) Angular UI / Directives / Sortable Table',
};
export default meta;
type Story = StoryObj<SortableTableExampleComponent>;

export const Primary: Story = {
    args: {},
};

export interface Pagination {
    currentPage: number;
    totalPages: number;
    pageSize: number;
}

export interface TableState {
    dataSource: any[];
    draggedColIndex: number | null;
    filterDataSource: { column: string; value: string };
    headers: string[];
    initialDataSource: any[];
    initialHeaders: string[];
    pagination: {
        currentPage: number;
        totalPages: number;
        pageSize: number;
    };
    preferences: {
        visibleColumns: string[];
        groupBy: string | undefined;
    };
    selectedRows: any[];
    sortDataSource: { key: string; direction: 'ascending' | 'descending' };
    [key: string]: any;
}

export interface RowData {
    [key: string]: any;
}

import { Directive, computed, signal } from '@angular/core';

export interface SortableItem {
    id: string;
    [key: string]: any;
}

@Directive({
    selector: '[adk-sorting]',
    exportAs: 'adkSorting',
    standalone: true,
})
export class AdkSorting<T extends SortableItem> {
    #items = signal<T[]>([]);
    #sortKey = signal<string | null>(null);
    #sortDirection = signal<'asc' | 'desc'>('asc');

    sortedItems = computed(() => {
        const items = [...this.#items()];
        const key = this.#sortKey();
        const direction = this.#sortDirection();

        if (!key) return items;

        return items.sort((a, b) => {
            const aValue = a[key];
            const bValue = b[key];

            if (aValue < bValue) {
                return direction === 'asc' ? -1 : 1;
            } else if (aValue > bValue) {
                return direction === 'asc' ? 1 : -1;
            } else {
                return 0;
            }
        });
    });

    /**
     * Add items to the list
     * @param newItems
     */
    add(...newItems: T[]): void {
        this.#items.update((items) => [...items, ...newItems]);
    }

    /**
     * Set the sorting key and direction
     * @param key
     * @param direction
     */
    sort(key: string, direction: 'asc' | 'desc'): void {
        this.#sortKey.set(key);
        this.#sortDirection.set(direction);
    }

    /**
     * Clear the sorting
     */
    clearSort(): void {
        this.#sortKey.set(null);
        this.#sortDirection.set('asc');
    }

    /**
     * Get the current sorting key
     */
    getSortKey(): string | null {
        return this.#sortKey();
    }

    /**
     * Get the current sorting direction
     */
    getSortDirection(): 'asc' | 'desc' {
        return this.#sortDirection();
    }
}

