export class DraggableUtil {
    headers: string[] = [];
    dataSource = [];
    draggedColIndex = null;
    selectedRows = [];

    // Function to handle dragging of columns
    dragRows(state: any, action: any) {
        const targetIndex = action.payload;
        const draggedColIndex = state.draggedColIndex;
        const dataSource = state.dataSource;
        if (draggedColIndex === null || draggedColIndex === targetIndex) return; // If no valid drag action, exit

        // Update headers array with dragged column
        const newHeaders = [...state.headers];
        const draggedHeader = newHeaders.splice(draggedColIndex, 1)[0];
        newHeaders.splice(targetIndex, 0, draggedHeader);

        // Update rows in dataSource array with dragged column
        const newRows = dataSource.map((row: any) => {
            const entries = Object.entries(row);
            const draggedEntry = entries.splice(draggedColIndex, 1)[0];
            entries.splice(targetIndex, 0, draggedEntry);
            return Object.fromEntries(entries); // Convert back to object
        });

        // Update state with new headers, rows, and reset draggedColIndex
        this.headers = newHeaders;
        this.dataSource = newRows;
        this.draggedColIndex = null;
        this.selectedRows = [];
    }
}

import { AbstractControl, FormControl, ValidatorFn, Validators } from '@angular/forms';
import { Identifiable, TYPE, VALUE } from './identifiable';
import { Observable, map } from 'rxjs';

export declare type AttributeEvent = (field: Field, event?: any) => void;

export declare type FieldSteps = Identifiable & {
    [additionalProperties: string]: any;
} & {
    step: number;
    label: string;
    active: boolean;
};

export declare type FieldOptions = Identifiable & {
    [additionalProperties: string]: any;
} & {
    value: string;
    label: string;
    checked?: boolean;
    description?: string;
    category?: string;
};

export type Field<
    Props = FieldProps & {
        [additionalProperties: string]: any;
    }
> = Identifiable & {
    type: TYPE;
    key: string;
    value: VALUE;
    description?: string;
    errorMessage?: string;
    hint?: string;
    label?: string;
    placeholder?: string;
    showError?: boolean;
    validation?: ValidatorFn[] | null;
    disabled?: boolean;
    props?: Props;
    formControl?: AbstractControl | { [key: string]: AbstractControl };
};

export type FieldProps = {
    autocomplete?: string;
    appearance?: string;
    class?: string;
    checked?: boolean;
    errorMessage?: string;
    labelClass?: string;
    inputClass?: string;
    options?: FieldOptions[];
    rows?: number;
    cols?: number;
    max?: number;
    min?: number;
    minLength?: number;
    isMultipleTag?: boolean;
    pattern?: string | RegExp;
    required?: boolean;
    tabindex?: number;
    attributes?: {
        [key: string]: string | number;
    };
    step?: number;
    focus?: AttributeEvent;
    blur?: AttributeEvent;
    keyup?: AttributeEvent;
    keydown?: AttributeEvent;
    click?: AttributeEvent;
    change?: AttributeEvent;
    keypress?: AttributeEvent;
};

export class FieldBuilder {
    private static idCounter = 0;

    private static generateId(): string {
        return `field_${this.idCounter++}`;
    }

    static createField(
        type: TYPE,
        key: string,
        value: any,
        label?: string,
        placeholder?: string,
        props?: FieldProps,
        description?: string,
        validation?: ValidatorFn[]
    ): Field {
        const id = this.generateId();
        const field: Field = {
            id,
            type,
            key,
            value,
            validation: validation || [],
            label: label || '',
            placeholder: placeholder || '',
            description: description || '',
            props: props || {},
        };
        const addedValidations = validation || [];
        field.validation = props?.required ? [Validators.required, ...addedValidations] : field.validation;
        field.formControl = field.type === 'checkbox' ? this.checkboxFormFields(field) : new FormControl(field.value, field.validation);
        return field;
    }

    static validators = {
        required: (required: boolean) => (required ? Validators.required : []),
        email: (email: boolean) => (email ? Validators.email : []),
        pattern: (pattern: string | RegExp) => (pattern ? Validators.pattern(pattern) : []),
    };

    static createOptionsField(
        type: TYPE,
        key: string,
        label: string,
        placeholder: string,
        options: FieldOptions[],
        props?: FieldProps,
        description?: string
    ): Field {
        const id = this.generateId();
        const field: Field = {
            id,
            type,
            key,
            value: '', // No initial value for options field
            label,
            placeholder,
            description: description || '',
            props: {
                options,
                ...props,
            },
        };
        const addedValidations = field.validation || [];
        field.validation = props?.required ? [Validators.required, ...addedValidations] : field.validation;
        field.formControl = field.type === 'checkbox' ? this.checkboxFormFields(field) : new FormControl(field.value, field.validation);
        return field;
    }

    static checkboxFormFields(field: Field): { [key: string]: AbstractControl } {
        const formControls: { [key: string]: AbstractControl } = {};
        field.props?.options?.forEach((option) => {
            formControls[option.id] = new FormControl('');
        });
        return formControls;
    }
}

