import { AdkList, AdkPagination, AdkSelection } from '../directives';
import { Directive, computed, inject } from '@angular/core';
import { ID, Identifiable } from '../models';
import { Observable, map } from 'rxjs';

@Directive({
  selector: '[adk-datasource]',
  exportAs: 'adkDatasource',
  standalone: true,
  /**
   * Important! Angular Directive composition API in action!
   */
  hostDirectives: [
    // { directive: AdkHttpClient, inputs: ['adkUrl', 'adkPage', 'adkLimit'] },
    AdkList,
    AdkSelection,
    AdkPagination,
  ],
})
export class AdkDatasource<T extends Identifiable> {
  // #httpClient = inject(AdkHttpClient, { self: true });
  #list = inject<AdkList<T>>(AdkList, { self: true });
  #selection = inject(AdkSelection, { self: true });
  #pagination = inject(AdkPagination, { self: true });

  /**
   * The list of datasource items
   */
  readonly items = this.#list.items;

  /**
   * Check if we are on the first page
   */
  readonly first = this.#pagination.first;

  /**
   * Check if we are on the last page
   */
  readonly last = this.#pagination.last;

  /**
   * The selected items of the selected datasource
   */
  readonly selectedItems = computed(() => this.items().filter((item) => this.#selection.selected(item.id)));

  async fetch(data: Observable<any[]>): Promise<void> {
    data
      .pipe(
        map((items) => {
          return items.map((item, i) => {
            return {
              ...item,
              id: item.id ?? i,
            };
          });
        })
      )
      .subscribe({
        next: (value: any[]) => this.#list.add(...value),
        complete: () => console.info('Fetched data successfully!'),
      });
  }

  /**
   * Select datasource by their ids
   * @param ids
   */
  select(...ids: ID[]): void {
    this.#selection.select(...ids);
  }

  /**
   * Select all datasource
   */
  selectAll(): void {
    this.#selection.select(...this.items().map((todo) => todo.id));
  }

  /**
   * Deselect all datasource
   */
  reset(): void {
    this.#selection.clear();
  }

  /**
   * Check if a todo is selected
   * @param id
   */
  selected(id: ID): boolean {
    return this.#selection.selected(id);
  }

  /**
   * Deselect a todo by its id
   * @param id
   */
  deselect(id: ID): void {
    this.#selection.deselect(id);
  }
}

import { Directive, EventEmitter, HostBinding, HostListener, Output, signal } from '@angular/core';

@Directive({
    selector: '[adkDraggable]',
    standalone: true,
})
export class AdkDraggableDirective {
    #isDragging = signal(false);

    @Output() dragStart = new EventEmitter<DragEvent>();
    @Output() dragEnd = new EventEmitter<DragEvent>();

    @HostBinding('draggable') draggable = true;

    @HostBinding('class.dragging')
    get isDragging() {
        return this.#isDragging();
    }

    @HostListener('dragstart', ['$event'])
    onDragStart(event: DragEvent) {
        this.#isDragging.set(true);
        this.dragStart.emit(event);
    }

    @HostListener('dragend', ['$event'])
    onDragEnd(event: DragEvent) {
        this.#isDragging.set(false);
        this.dragEnd.emit(event);
    }
}

import { Directive, EventEmitter, HostBinding, HostListener, Output, signal } from '@angular/core';

@Directive({
    selector: '[adkDroppable]',
    standalone: true,
})
export class AdkDroppableDirective {
    #isOver = signal(false);

    @Output() drop = new EventEmitter<DragEvent>();

    @HostBinding('class.over')
    get isOver() {
        return this.#isOver();
    }

    @HostListener('dragover', ['$event'])
    onDragOver(event: DragEvent) {
        event.preventDefault();
        this.#isOver.set(true);
    }

    @HostListener('dragleave', ['$event'])
    onDragLeave(_event: DragEvent) {
        this.#isOver.set(false);
    }

    @HostListener('drop', ['$event'])
    onDrop(event: DragEvent) {
        event.preventDefault();
        this.#isOver.set(false);
        this.drop.emit(event);
    }
}

import { Directive, computed, signal } from '@angular/core';
import { Field, ID } from '../models';

@Directive({
  selector: '[adz-field-builder]',
  exportAs: 'adkFieldList',
  standalone: true,
})
export class AdkFieldList<T extends Field> {
  #fields = signal<Record<ID, T>>({});

  readonly fields = computed(() => Object.values(this.#fields()));

  /**
   * Get a field by id
   * @param id
   */
  get(id: ID): T | undefined {
    return this.#fields()[id];
  }

  /**
   * Add new fields to the list
   * @param newItems
   */
  add(...newFields: T[]): void {
    this.#fields.update((fields) => newFields.reduce((accumulator, field) => ({ ...accumulator, [field.id]: field }), fields));
  }

  /**
   * Update an field in the list
   * @param field
   */
  update(field: T): void {
    this.#fields.update((fields) => ({ ...fields, [field.id]: field }));
  }

  /**
   * Remove an field from the list
   * @param field
   */
  remove(field: T): void {
    this.#fields.update((fields) => {
      const { [field.id]: _, ...rest } = fields;
      return rest;
    });
  }

  /**
   * Clear all fields from the list
   */
  clear(): void {
    this.#fields.set({});
  }
}

import { Directive, inject } from '@angular/core';

import { AdkFieldList } from './field-list';
import { Field } from '../models';

@Directive({
  selector: '[adk-fields]',
  exportAs: 'adkFields',
  standalone: true,
  hostDirectives: [AdkFieldList],
})
export class AdkFields<T extends Field> {
  #fields: AdkFieldList<T> = inject<AdkFieldList<T>>(AdkFieldList, { self: true });

  /**
   * The list of fields
   */
  readonly items = this.#fields.fields;

  /**
   * Set fields
   */
  async setFields(fields: T[]): Promise<void> {
    this.#fields.add(...fields);
  }
}

import { AbstractControl, FormGroup } from '@angular/forms';
import { Directive, WritableSignal, signal } from '@angular/core';
import { Field, FieldOptions } from '../models';

@Directive({
  selector: '[adk-form-group]',
  exportAs: 'adkFormGroup',
  standalone: true,
})
export class AdkFormGroup<T extends Field> {
  #formGroup = signal<FormGroup<any>>(new FormGroup({}));

  /**
   * The form group
   */
  formGroup: WritableSignal<FormGroup<any>> = this.#formGroup;

  /**
   * Set the form group
   */
  setFormGroup(fields: T[]): void {
    const formControls = fields.reduce((accumulator: any, field) => {
      if (field.formControl instanceof AbstractControl) {
        accumulator[field.key] = field.formControl;
      } else if (field.formControl instanceof Object) {
        // object of form controls
        field.props?.options?.forEach((option: FieldOptions) => {
          const key = option.id;
          accumulator[key] = (field.formControl as { [key: string]: AbstractControl<any, any> })[key];
        });
      }
      return accumulator;
    }, {});

    const formGroup = new FormGroup(formControls);
    this.#formGroup.set(formGroup);
  }
}

import { Directive, inject, input } from '@angular/core';

import { HttpClient } from '@angular/common/http';
import { firstValueFrom } from 'rxjs';

@Directive({
  selector: '[adk-http-client]',
  exportAs: 'adkHttpClient',
  standalone: true,
})
export class AdkHttpClient {
  #http = inject(HttpClient);
  /**
   * The URL to send the request to
   */
  url = input.required<string>({ alias: 'adkUrl' });
  /**
   * The page to get data from
   */
  page = input(1, { alias: 'adkPage' });
  /**
   * The number of items to get
   */
  limit = input(10, { alias: 'adkLimit' });
  /**
   * Get data from the server
   * @param page
   */
  async get<T>(page = { page: 1, limit: 10 }): Promise<{ total: number; items: T[] }> {
    const searchParams = new URLSearchParams({
      _page: page.page.toString(),
      _per_page: page.limit.toString(),
    }).toString();

    const response = await firstValueFrom(
      this.#http.get<T[]>(`${this.url()}?${searchParams}`, {
        observe: 'response',
      })
    );
    const total = parseInt(response.headers.get('X-Total-Count') ?? '0', 10);
    const items = response.body!;

    return { total, items };
  }
}

import { Directive, computed, signal } from '@angular/core';
import { ID, Identifiable } from '../models';

@Directive({
  selector: '[adz-list]',
  exportAs: 'adkList',
  standalone: true,
})
export class AdkList<T extends Identifiable> {
  #items = signal<Record<ID, T>>({});
  readonly items = computed(() => Object.values(this.#items()));

  /**
   * Get an item by id
   * @param id
   */
  get(id: ID): T | undefined {
    return this.#items()[id];
  }

  /**
   * Add new items to the list
   * @param newItems
   */
  add(...newItems: T[]): void {
    this.#items.update((items) =>
      newItems.reduce((accumulator, item) => {
        return { ...accumulator, [item.id]: item };
      }, items)
    );
  }

  /**
   * Update an item in the list
   * @param item
   */
  update(item: T): void {
    this.#items.update((items) => ({ ...items, [item.id]: item }));
  }

  /**
   * Remove an item from the list
   * @param item
   */
  remove(item: T): void {
    this.#items.update((items) => {
      const { [item.id]: _, ...rest } = items;
      return rest;
    });
  }

  /**
   * Clear all items from the list
   */
  clear(): void {
    this.#items.set({});
  }
}

import { Directive, computed, signal } from '@angular/core';

@Directive({
  selector: '[adk-pagination]',
  exportAs: 'adkPagination',
  standalone: true,
})
export class AdkPagination {
  #page = signal(1);
  readonly page = computed(() => this.#page());
  readonly limit = signal(10);
  readonly total = signal(0);

  /**
   * Check if the current page is the first
   */
  first = computed(() => this.#page() === 1);

  /**
   * Check if the current page is the last
   */
  last = computed(() => this.#page() * this.limit() >= this.total());

  /**
   * Go to the next page
   */
  next(): void {
    if (this.last()) {
      throw new Error('You are already on the last page');
    }
    this.#page.update((page) => page + 1);
  }

  /**
   * Go to the previous page
   */
  previous(): void {
    if (this.first()) {
      throw new Error('You are already on the first page');
    }
    this.#page.update((page) => page - 1);
  }
}

import { Directive, computed, signal } from '@angular/core';

import { ID } from '../models';

@Directive({
  selector: '[adk-selection]',
  exportAs: 'adkSelection',
  standalone: true,
})
export class AdkSelection {
  #items = signal<Record<ID, boolean>>({});
  count = computed(() => Object.values(this.#items()).filter(Boolean).length);

  /**
   * Select multiple items
   * @param ids
   */
  select(...ids: ID[]): void {
    this.#items.update((items) => ids.reduce((accumulator, id) => ({ ...accumulator, [id]: true }), items));
  }

  /**
   * Deselect an item
   * @param id
   */
  deselect(id: ID): void {
    this.#items.update((items) => {
      const { [id]: _, ...rest } = items;
      return rest;
    });
  }

  /**
   * Clear all selected items
   */
  clear(): void {
    this.#items.set({});
  }

  /**
   * Check if an item is selected
   * @param id
   */
  selected(id: ID): boolean {
    return this.#items()[id] ?? false;
  }
}

import { Directive, computed, signal } from '@angular/core';

export interface SortableItem {
    id: string;
    [key: string]: any;
}

@Directive({
    selector: '[adk-sorting]',
    exportAs: 'adkSorting',
    standalone: true,
})
export class AdkSorting<T extends SortableItem> {
    #items = signal<T[]>([]);
    #sortKey = signal<string | null>(null);
    #sortDirection = signal<'asc' | 'desc'>('asc');

    sortedItems = computed(() => {
        const items = [...this.#items()];
        const key = this.#sortKey();
        const direction = this.#sortDirection();

        if (!key) return items;

        return items.sort((a, b) => {
            const aValue = a[key];
            const bValue = b[key];

            if (aValue < bValue) {
                return direction === 'asc' ? -1 : 1;
            } else if (aValue > bValue) {
                return direction === 'asc' ? 1 : -1;
            } else {
                return 0;
            }
        });
    });

    /**
     * Add items to the list
     * @param newItems
     */
    add(...newItems: T[]): void {
        this.#items.update((items) => [...items, ...newItems]);
    }

    /**
     * Set the sorting key and direction
     * @param key
     * @param direction
     */
    sort(key: string, direction: 'asc' | 'desc'): void {
        this.#sortKey.set(key);
        this.#sortDirection.set(direction);
    }

    /**
     * Clear the sorting
     */
    clearSort(): void {
        this.#sortKey.set(null);
        this.#sortDirection.set('asc');
    }

    /**
     * Get the current sorting key
     */
    getSortKey(): string | null {
        return this.#sortKey();
    }

    /**
     * Get the current sorting direction
     */
    getSortDirection(): 'asc' | 'desc' {
        return this.#sortDirection();
    }
}

